import React,{useState} from 'react';
import {ValidateUser} from '../../services/LoginService';
import {setCustomerDetails} from '../../common/token';
import {useHistory} from 'react-router-dom';

const Login=()=>{
    const[loginDetails,setLoginDetails]=useState({userName:'',userPassword:''});
    
   const handleInput = (event) => {
      setLoginDetails((prev) => ({
          ...prev,
          [event.target.name]: event.target.value,
        }));
    };
   const signInUser=async(event)=>{
    event.preventDefault();
    const history=useHistory();
    const details =  await ValidateUser(loginDetails);
    setCustomerDetails(details);
    if(details && details.customerDetails){
      let path='price/estimate';
      history.push(path);
    }
    }

    return (
<div>
        <form onSubmit={signInUser}>
          <label>
              <p>Username</p>
          <input
            type="text"
            placeholder="Enter Username"
            name="userName"
            value={loginDetails.userName}
            onChange={handleInput}
          />
          </label>
          <label>
          <p>Password</p> 
          <input
            type="password"
            placeholder="Enter Password"
            name="userPassword"
            value={loginDetails.userPassword}
            onChange={handleInput}
          /></label>
          <div>
          <button type="submit">Login</button>
          <button type="reset">Cancel</button>
          </div>
        </form>
      </div>
    );
};
export default Login;
