import {useState} from 'react';
import { getCustomerDetails } from "../../common/token.js";
const Estimation = () => {
  const customerDetails = getCustomerDetails();
const handleOperation=(actionType)=>{
alert(actionType);
}  
  return (
    <div>
      <label>Welcome {customerDetails.type} User</label>
      <lable>
        <p>Gold Price (per gram)</p>
        <input type="text" placeholder="Enter Price" name="price" />
      </lable>

      <lable>
        <p>Weight (grams)</p>
        <input type="text" placeholder="Enter Weight" name="weight" />
      </lable>
      <lable>
        <p>Total Price</p>
        <input readOnly={true} type="text" name="totalPrice" />
      </lable>
      {customerDetails && customerDetails.discountRule.itemVisible ? (
        <lable>
          <p>Discount %</p>
          <input
            type="text"
            name="pricePercentage"
            readOnly={customerDetails.discountRule.applicable}
            value={customerDetails.discountRule.pricePercentage}
          />
        </lable>
      ) : null}
      <label>
          <button type="button" onClick={()=>handleOperation("calculate")}>Calculate</button>
          <button type="button" onClick={()=>handleOperation("printtoscreen")}>Print to Screen</button>
          <button type="button" onClick={()=>handleOperation("printtofile")}>Print to File</button>
          <button type="button" onClick={()=>handleOperation("printtopaper")}>Print to Paper</button>
          <button type="button">Close</button>
      </label>
    </div>
  );
};
export default Estimation;
