import { useState } from "react";
import { getCustomerDetails } from "../../common/token.js";
import {PrinttoFile,PrinttoPaper,} from './EstimationAction'

  const Estimation = () => {
  const customerDetails = getCustomerDetails();
  const discountItem=customerDetails.discountRule;
  const apiModel={
    customerId:'',
    customerType:'',
    discountPercentage:'',
    itemDetails:{
        price: '',
        weight: '',
        totalPrice: '',
    },
    totalAmountAfterDiscount:''
  };

  const [estimattedItem, setEstimattedItem] = useState(apiModel);
  const handlePriceInput = (event) => {
    setEstimattedItem((prev) => ({
        itemDetails:{
            ...prev.itemDetails,
            price: event.target.value,
            totalPrice:event.target.value * estimattedItem.itemDetails.weight
        }
    }));
  };

  const handleWeightInput = (event) => {
  setEstimattedItem((prev) => ({
    itemDetails:{
        ...prev.itemDetails,
        weight: event.target.value,
        totalPrice:event.target.value * estimattedItem.itemDetails.price
    }
}));
};

  const handleOperation = (actionType) => {
   const permissionToGetDiscount= customerDetails.discountRule.applicable;
    const totalAmountAfterDiscount=permissionToGetDiscount?calculateDiscountPrice():estimattedItem.itemDetails.totalprice;
    estimattedItem.customerId=customerDetails?.id;
    estimattedItem.customerType=customerDetails?.type;
    estimattedItem.discountPercentage=totalAmountAfterDiscount;
    switch(actionType){
        case 'printtoscreen':
            console.log(estimattedItem);
            break;
            case 'printtofile':
                console.log(estimattedItem);
                break;
                case 'printtopaper':
                    console.log(estimattedItem);
                    break;
                    case 'calculatetotalamount':
                        console.log(estimattedItem);
                        break;
                        default:
                            break;
    }
  };
 
  const calculateDiscountPrice=()=>{
      let totalPrice=calculateTotalPrice();
      let discountPrice=totalPrice-((totalPrice*discountItem.pricePercentage)/100);
      return  discountPrice; 
  }

  const calculateTotalPrice=()=>{
    let totalprice=estimattedItem.itemDetails.price * estimattedItem.itemDetails.weight;
    return totalprice;
  }
  return (
    <div>
      <label>Welcome {customerDetails.type} User</label>
      <form>
      <lable>
        <p>Gold Price (per gram)</p>
        <input
          type="text"
          value={estimattedItem.itemDetails.price}
          onChange={handlePriceInput}
          placeholder="Enter Price"
          name="price"
        />
      </lable>

      <lable>
        <p>Weight (grams)</p>
        <input
          type="text"
          value={estimattedItem.itemDetails.weight}
          onChange={handleWeightInput}
          placeholder="Enter Weight"
          name="weight"
        />
      </lable>
      <lable>
        <p>Total Price</p>
        <input
          readOnly={true}
          value={estimattedItem.itemDetails.totalPrice}
          type="number"
          name="totalPrice"
        />
      </lable>
      {customerDetails && customerDetails.discountRule.itemVisible ? (
        <lable>
          <p>Discount %</p>
          <input
            type="text"
            name="pricePercentage"
            readOnly={customerDetails.discountRule.applicable}
            value={customerDetails.discountRule.pricePercentage}
          />
        </lable>
      ) : null}</form>
      <label>
        <button type="button" onClick={() => handleOperation("calculatetoamount")}>
          Calculate
        </button>
        <button type="button" onClick={() => handleOperation("printtoscreen")}>
          Print to Screen
        </button>
        <button type="button" onClick={() => handleOperation("printtofile")}>
          Print to File
        </button>
        <button type="button" onClick={() => handleOperation("printtopaper")}>
          Print to Paper
        </button>
        <button type="button">Close</button>
      </label>
    </div>
  );
};
export default Estimation;
