import { useState } from "react";
import { getCustomerDetails } from "../../common/token.js";
const Estimation = () => {
  const customerDetails = getCustomerDetails();
  const discountItem=customerDetails.discountRule;
  const [estimattedItem, setEstimattedItem] = useState({
    price: "",
    weight: "",
    totalPrice: "",
  });

  const handleInput = (event) => {
      debugger;
      let totalPrice=estimattedItem.totalprice===undefined?'':estimattedItem.totalprice;
    setEstimattedItem((prev) => ({
      ...prev,
      [event.target.name]: event.target.value,
      totalPrice:totalPrice==''?totalPrice:event.target.value* totalPrice
    }));
   
  };
  const handleOperation = (actionType) => {
    alert(`actionType ${calculateDiscountPrice()}`);

  };
 
  const calculateDiscountPrice=()=>{
      let totalPrice=calculateTotalPrice();
      let discountPrice=totalPrice-((totalPrice*discountItem.pricePercentage)/100);
      return  discountPrice; 
  }
  const calculateTotalPrice=()=>{
      debugger;
    let totalprice=estimattedItem.price * estimattedItem.weight;
    return totalprice;
  }
  return (
    <div>
      <label>Welcome {customerDetails.type} User</label>
      <form>
      <lable>
        <p>Gold Price (per gram)</p>
        <input
          type="text"
          value={estimattedItem.price}
          onChange={handleInput}
          placeholder="Enter Price"
          name="price"
        />
      </lable>

      <lable>
        <p>Weight (grams)</p>
        <input
          type="text"
          value={estimattedItem.weight}
          onChange={handleInput}
          placeholder="Enter Weight"
          name="weight"
        />
      </lable>
      <lable>
        <p>Total Price</p>
        <input
          readOnly={true}
          value={estimattedItem.totalPrice}
          type="number"
          name="totalPrice"
        />
      </lable>
      {customerDetails && customerDetails.discountRule.itemVisible ? (
        <lable>
          <p>Discount %</p>
          <input
            type="text"
            name="pricePercentage"
            readOnly={customerDetails.discountRule.applicable}
            value={customerDetails.discountRule.pricePercentage}
          />
        </lable>
      ) : null}</form>
      <label>
        <button type="button" onClick={() => handleOperation("calculate")}>
          Calculate
        </button>
        <button type="button" onClick={() => handleOperation("printtoscreen")}>
          Print to Screen
        </button>
        <button type="button" onClick={() => handleOperation("printtofile")}>
          Print to File
        </button>
        <button type="button" onClick={() => handleOperation("printtopaper")}>
          Print to Paper
        </button>
        <button type="button">Close</button>
      </label>
    </div>
  );
};
export default Estimation;
